     const login = (item) => {
         const cargoUtil = JSON.stringify(item);
         alert(cargoUtil);
         fetch("http://localhost/IniciaSessio.php",{
             method:"POST",
             headers: {
                 "Content-Type": 'application/json'
             },
             body: cargoUtil
         }).then(res=>res.json()
         ).then((res)=>setJWT(res))
             .catch(err => alert(err))};

