import React, {useCallback, useContext, useState} from 'react';
import Context from "../Context/UserContextProvider";

export default function useTiquets () {
    const {jwt} = useContext(Context);
    const [tiquets,setTiquets] = useState();

    const compraTiquets = useCallback((dies) => {
        const item = {};
        item['dies'] = dies;
        item['jwt'] = jwt;
        item['pagat'] = false;
        const carga = JSON.stringify(item);
        console.log(carga);
        fetch("http://localhost/CompraTiquets.php", {
            method: "POST",
            headers: {
                "Content-Type": 'application/json'
            },
            body: carga
        }).then(res => {
            return res.json()
        }).then((res) => {
            if(!res){
                alert("Error en la compra");
            }else {
                setTiquets(res);
            }
        });
    })

    const vistaTiquets = useCallback(() => {
        const carga = JSON.stringify(jwt);
        fetch("http://localhost/VistaTiquets.php", {
            method: "POST",
            headers: {
                "Content-Type": 'application/json'
            },
            body: carga
        }).then(res => {
            return res.json()
        }).then((res) => {
            if(!res){
                alert("Error al carregar els tiquets");
            }else {
                alert(res);
                setTiquets(res);
            }
        });
    })


    return{
        compraTiquets,
        tiquets,
        vistaTiquets
    }
};
