import React, {useEffect} from "react";
import ServeiVistaInfant from "../Servicio/Servei_VistaInfant";
import {Button} from "react-bootstrap";

export default function VistaInfant() {
    const {profile, getInfant, fill} = ServeiVistaInfant();

    useEffect(() => {
        getInfant();
    },[fill])

    const handleClick = (item) => {

    };

    return(
        <div>
        { fill ?
            <div className="container">
                <div className="row Infant">
                    <div className="col">
                        <h4>{profile.Nom}</h4>
                        <h5>Edat: {profile.Edat}</h5>
                    </div>
                    <div className="col">
                        <h5>{profile.Congnom}</h5>
                        <p>Curs: {profile.Curs}</p>
                    </div>
                    <div className="col">
                        <h5>Observacions:</h5>
                        <p>{profile.Observacions}</p>
                    </div>
                    <div className="col">
                        <h5>Persona de Contacte: </h5>
                        <p>Tutor: {profile.Tutor}</p>
                        <p>Telefon: {profile.telefon}</p>
                    </div>
                    <div className="col">
                        <Button variant="danger" onClick={handleClick(profile.Nom)}>Elimina</Button>
                    </div>
                </div>
            </div> : <h1>Afegeix al teu fill</h1>
        }
        </div>
    )

}
