import React, {useEffect} from "react";
import ServeiTotsInfants from "../Servicio/Servei_TotsInfants";
import VistaInfant from "./VistaInfant";
import {Accordion, Button} from "react-bootstrap";
import './TotsInfants.css';

export default function  TotsInfants () {

    const {profiles, getInfants, subscrits} = ServeiTotsInfants();
    const cursos = ["P3","P4","P5","1r","2n","3r","4t","5e","6e"];

    useEffect(() => {
        cursos.map((curs) => {
            getInfants(curs);
        } )
    },[])

    const viewInfant = () =>{

        if(subscrits && profiles != false) {
            return cursos.map((curs) => {
                getInfants(curs);
                profiles.map((profile) =>
                    <li>
                        <Accordion>
                            <Accordion.Toggle className="NomInfant" eventKey="0" as={Button} variant={"link"}>
                                <p>{profile.Nom} {profile.Congnom}</p>
                            </Accordion.Toggle>
                            <Accordion.Collapse className="container collapseALL" eventKey="0">
                                <p>Observacions: {profile.Observacions} Tutor: {profile.Tutor}  Tel√®fon:{profile.telefon}</p>
                            </Accordion.Collapse>
                        </Accordion>
                    </li>
                )
            } )
        }
        else {
            return <h1>No hi ha infants</h1>
        }
    }

    return(
        <div className="container">
            <div className="row">
                <div className="col-md-12">
                    <div className="section-heading">
                        <h2>Tots els Infants</h2>
                    </div>
                </div>

            </div>

            <div className="row">
                <div className="col-md-12">
                    <ul>{viewInfant()}</ul>
                </div>
            </div>

        </div>
    )

}
