import React from "react";
import './ListDays.css';
import {Button} from "react-bootstrap";

export default class ListDays extends React.Component {
    render() {
        return (<div className="ListaDias">
            <h5>Dies Seleccionats</h5>
            <ul>
                {this.props.days.map((f) =>
                    <div className="row">
                        <div className="col-auto"> <li>{f.getDate() + "/" + (f.getMonth() +1) + "/" + f.getFullYear()} </li></div>

                        <div className="col-4"><Button className="btn-sm" variant="danger">x</Button></div>
                    </div>
                   )}
            </ul>
        </div>)
    }
}
