import React, {useEffect} from "react";
import {Form} from "react-bootstrap";
import useMenu from "../Servicio/Servei_Menu";

export default function VistaMenu() {
    const {vistaMenu, profilesmenus} = useMenu();

    useEffect(() => {}, [profilesmenus])

    const handleChange = ({target}) =>{
        const  {value} = target;
        vistaMenu(value);
    }

    const Menus = () =>{
        if(profilesmenus !== null){
            return profilesmenus.map((p) => <div className="col align-self-center">
                <h4>{p.IDtipus}</h4>
                <ul>
                    <li>Primer: {p.Primer}</li>
                    <li>Segon: {p.Segon}</li>
                    <li>Postre: {p.Postre}</li>
                </ul>
            </div>)
        }else {
            return <div className="col">
                <p>Encara no s'han afegit men√∫s d'aquest dia.</p>
            </div>
        }
    }


    return (<><div className="abs-center">
        <Form className="p-2 form">
            <Form.Group controlId="Data">
                <Form.Control type="date" name="Data" onChange={handleChange} />
            </Form.Group>
        </Form>
    </div>
        <div className="container">
            <div className="row">
                {Menus()}
            </div>
        </div>
    </>);
}
