import React, {useEffect, useState} from "react";
import './ListDays.css';
import {Button} from "react-bootstrap";

export default function ListDays ({days, funcion}) {

    const listDays = days;

    const eliminaDia = (index) => {
        listDays.splice(index,1);
    }

    return (<div className="ListaDias">
                <h5>Dies Seleccionats</h5>
                <ul className="container">
                    {listDays.map((f, i) =>
                        <div className="row">
                            <div className="col-6">
                                <li>{f.getDate() + "/" + (f.getMonth() + 1) + "/" + f.getFullYear()} </li>
                            </div>
                            <div className="col-4"><Button className="btn-sm" variant="danger" onClick={() => eliminaDia(i)}>X</Button></div>
                        </div>
                    )}
                </ul>
            </div>
        )
    }
