import React, {useState} from 'react';
import {Button, Modal, Table} from "react-bootstrap";
import './PopupCompra.css';

export default function PopupCompra ({Dies, profiles}) {
    const [show, setShow] = useState(false);
    const [diaInfant, setDiaInfant] = useState({});

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const handleSubmit = (event) => {
        event.preventDefault();
        console.log(diaInfant);
    }

    const handleChange = ({ target }) => {
        const  {name, value} = target;
        console.log(value['Alumne']);
        setDiaInfant((prev) => ({
            ...prev,
            [name]: [...prev,value]
        }));
    };

    const tranformatext = (f) => {
        const dia = f.getDate() + "/" + (f.getMonth() + 1) + "/" + f.getFullYear();
        return dia;
    }

    const tablaDias = () => {
        return Dies.map((f) => <tr>
            <td>
                {tranformatext(f)}
            </td>
            {tablaInfant(f)}
        </tr>)
    }

    const tablaInfant = (f) => {
        return profiles.map((p) => <td className="form-group">
                <input type="checkbox" id={p.idAlumne} name={p.idAlumne} value={f} defaultChecked={"yes"} onChange={handleChange}/>
                <label htmlFor={p.idAlumne}/>
            </td>
        )
    }


    return (
        <>
            <Button variant="primary" onClick={handleShow}>
                Compra
            </Button>
            <Modal show={show}
                   aria-labelledby="contained-modal-title-vcenter"
                   centered
                   className="PopupCompra"
                   onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Revisa</Modal.Title>
                </Modal.Header>
                <form onSubmit={handleSubmit}>
                    <Modal.Body>
                        {Dies.length === 0 ? <h4>Selecciona els dies</h4> :
                            <>
                                <Table>
                                    <thead>
                                    <tr>
                                        <td></td>
                                        {profiles !== null ? profiles.map((p) => <th>{p.Nom}</th>) : <h4>Afegeix als teus infants</h4>}
                                    </tr>
                                    </thead>
                                    <tbody>
                                        {tablaDias()}
                                    </tbody>
                                </Table>
                                <h4>Total: </h4>
                            </>}
                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={handleClose}>
                            Close
                        </Button>
                        <Button variant="primary" type="submit">
                            Save Changes
                        </Button>
                    </Modal.Footer>
                </form>
            </Modal>
        </>
    );
}
