import React, {useState, useEffect} from 'react';
import './Login.css'


export default function Login(){
    const [profile, setProfile] = useState({});


    const handleChange = ({ target }) => {
        const  {name, value} = target;
        setProfile((prevProfile) => ({
            ...prevProfile,
            [name]: value
        }));
    };

    return(
        <div className="login-page">
            <div className="page-heading header-text">
                <div className="container">
                    <div className="row">
                        <div className="col-md-12">
                            <h1 >Login</h1>
                        </div>
                    </div>

                </div>
            </div>
            <div className="container">
                <div className="row">
                    <form className="col-md-12">
                        <label className="col-12">
                            Usuari:
                            <input type="password" name="password" placeholder="Password" value={profile.usuari}  onChange={handleChange} />
                        </label>
                        <label  className="col-12">
                            Contrasenya:
                            <input  type="password" name="password" placeholder="Password" value={profile.pass}  onChange={handleChange}/>
                        </label>
                        <input  className="col-12" type="submit" value="Submit" />
                    </form>
                </div>
            </div>

        </div>

    );

}
