import React, {useEffect, useState} from "react";
import 'react-calendar/dist/Calendar.css';
import {ToggleButtonGroup, ToggleButton} from "react-bootstrap";
import ListDays from "./ListDays";

export default function Calendari ({Index, Any}) {
    const dies_text = ["DL", "DT", "DC", "DJ", "DV", "DS", "DG"];
    const [value, setValue] = useState([]);

    useEffect(() => {console.log(value)},[value])

    const handleChange = (val) => {setValue(val);};


    const createMes = (any, iMes) => {
        return (createTabla(any,iMes))
    }

    const createTabla = (any,iMes) => {

        return <table>
                {createTitolDies()}
                {createSetmanes(any,iMes)}

        </table>

    }

    const createTitolDies = () => {
        return (
            <tr>
                {dies_text.map((dia) => <th>{dia}</th>)}
            </tr>
        )
    }

    const createSetmanes = (any,iMes) =>{
        const fechas = numerate(any,iMes);
        const dia_actual = new Date();
        let counter = 0;
        const dias_mes = diasEnUnMes(iMes+1,any);
        const setmanes = [];

        for(let i = 0; i < 6; i++){
            const dies = [];
            for(let j = 0; j < 7; j++){
                if(counter < dias_mes) {
                    const fecha = fechas[counter];
                    let trobat = false;
                    let dia_semana = fecha.getDay();

                    if(dia_semana === 0){
                        dia_semana = 6;
                    }else {
                        dia_semana--;
                    }

                    if(fecha.getDay() === dia_actual.getDay() && fecha.getDate() === dia_actual.getDate() && fecha.getMonth() === dia_actual.getMonth()){
                        trobat = true;
                    }

                    if(dia_semana === j){
                        if(dia_semana === 5 || dia_semana === 6){
                            dies.push(<td>
                                <ToggleButtonGroup type="checkbox" defaultValue={[1, 3]} >
                                    <ToggleButton  className={trobat ? "activation" : null} variant="dark" disabled>{fecha.getDate()}</ToggleButton>
                                </ToggleButtonGroup>
                            </td>)
                        }else{
                            if(fecha < dia_actual ){
                                dies.push(<td>
                                    <ToggleButtonGroup type="checkbox" >
                                        <ToggleButton  className={trobat ? "activation" : null} disabled>{fecha.getDate()}</ToggleButton>
                                    </ToggleButtonGroup>
                                </td>)
                            }else {
                                dies.push(<td>
                                    <ToggleButtonGroup type="checkbox" value={value} onChange={handleChange}>
                                        <ToggleButton value={fecha} >{fecha.getDate()}</ToggleButton>
                                    </ToggleButtonGroup>
                                </td>)
                            }
                        }
                        counter++;
                    }else {
                        dies.push(<td></td>);
                    }
                }
            }
            setmanes.push(<tr>{dies}</tr>)
        }

        return(setmanes)
    }



    function DataPerDia(any,iMes,dia) {
        var date = new Date(any,iMes, dia);
        return new Date(date.setDate(dia))
    }

    function numerate(any,iMes){
        let fechas = []
        for (let i = 1; i <= diasEnUnMes(iMes+1, any); i++) {
            let fecha = DataPerDia(any,iMes, i);
            fechas.push(fecha);
        }
        return (fechas)
    }

    function diasEnUnMes(mes, any) {
        return new Date(any, mes, 0).getDate();
    }

    const eliminaDia = (index) => {
        console.log(value)
        console.log(index)

        setValue(prevState => prevState.splice(index,1));
        console.log(value)

    }

    return(<>
        <div className="row justify-content-center">
            <div className="col-auto margin">
                {createMes(Any, Index)}
            </div>
            <div className="col-auto">
                <ListDays days={value} funcion={eliminaDia}/>
            </div>
        </div>

    </>)
}
