import React, {useEffect} from "react";
import 'react-calendar/dist/Calendar.css';
import {forEach} from "react-bootstrap/ElementChildren";
import {Button} from "react-bootstrap";

export default function Calendari () {
    const dies_text = ["DL", "DT", "DC", "DJ", "DV", "DS", "DG"];
    const mesos_text = ["Gener", "Febrer", "MarÃ§", "Abril", "Maig", "Juny", "Juliol", "Agost", "Setembre", "Octubre", "Novembre", "Desembre"];
    let iMes = null;

    useEffect(() => {
        createAny("2021")
    }, [iMes])

    const createAny = (any) => {
        const dia_actual = new Date();
        if(iMes === null){
            iMes = dia_actual.getMonth();
        }
        const mes = mesos_text[iMes];
        return (
            <div className="Mes">
                <h1>{any}</h1>
                <div className="row">
                    <div className="col"><Button onClick={() => iMes--}>-</Button></div>
                    <div className="col"><h2>{mes}</h2></div>
                    <div className="col"><Button onClick={() => iMes++}>+</Button></div>
                </div>
                {createMes(any, mes, iMes)}
            </div>
        );
    }

    const createMes = (any,mes, iMes) => {
        return (createTabla(any,iMes))
    }

    const createTabla = (any,iMes) => {

        return <table>
            {createTitolDies()}
            {createSetmanes(any,iMes)}
        </table>
    }

    const createTitolDies = () => {
        return (
            <tr>
                {dies_text.map((dia) => <th>{dia}</th>)}
            </tr>
        )
    }

    const createSetmanes = (any,iMes) =>{
        let fechas = numerate(any,iMes);
        let counter = 0;
        const setmanes = [];
        for(let i = 0; i < 6; i++){
            const dies = [];
            for(let j = 0; j < 7; j++){
                if(counter < diasEnUnMes(iMes, any)) {
                    let fecha = fechas[counter];
                    if(fecha === 0){
                        fecha = 6;
                    }else {
                        fecha--;
                    }
                    if(fecha === j){
                        dies.push(<td>
                            <Button type="checkbox">{counter + 1}</Button>
                        </td>)
                        counter++;
                    }else {
                        dies.push(<td></td>);
                    }
                }
            }
            setmanes.push(<tr>{dies}</tr>)
        }

        return(setmanes)
    }



    function DataPerDia(any,iMes,dia) {
        var date = new Date(any,iMes, dia);
        return new Date(date.setDate(dia))
    }

    function numerate(any,iMes){
        let fechas = []
        for (let i = 1; i < diasEnUnMes(iMes, any); i++) {
            let fecha = DataPerDia(any,iMes, i);
            fechas.push(fecha.getDay());
        }
        return (fechas)
    }

    function diasEnUnMes(mes, any) {
        return new Date(any, mes, 0).getDate();
    }


    return <div>
                {createAny("2021")}

    </div>
}
