import React, {useState, useEffect} from 'react';
import './Login.css';
import {Button} from "reactstrap";
import Header from "./Components/Header";
import {useLocation} from "wouter";

export default function Login(){
    const [profile, setProfile] = useState({});
    const [, navigate] =useLocation();

    const handleChange = ({ target }) => {
        const  {name, value} = target;
        setProfile((prevProfile) => ({
            ...prevProfile,
            [name]: value
        }));
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        alert(JSON.stringify(profile));
        const cargaUtil = JSON.stringify(profile);
        const res = fetch("https://localhost/IniciaSessio.php", {
            method:'POST',
            headers: {
                "Content-Type": "application/json"
            },
            body: cargaUtil
        }).then(res => {
                if (!res.ok) throw new Error("Error a l'iniciar sessi칩")
                return res.json()
            }).then((res) => { ;
                if(res) navigate('/');
            })
    };



    return(
        <div>
            <div className="container">
                <div className="abs-center">
                    <form onSubmit={handleSubmit} className=" p-2 form">
                        <div className="form-group">
                            <h4>Inicia sessi칩</h4>
                        </div>
                        <div className="form-group">
                            <label htmlFor="email">Usuari</label>
                            <input value={profile.usuari || ''} type="text" name="usuari" id="email" className="form-control" onChange={handleChange}/>
                        </div>
                        <div className="form-group">
                            <label htmlFor="password">Contrase침a</label>
                            <input value={profile.contrasena || ''} type="password" name="contrasena" id="password" className="form-control" onChange={handleChange}/>
                        </div>
                        <button type="submit" className="btn btn-dark">Login</button>
                    </form>
                </div>
            </div>

        </div>

    );

}
