import React, {useEffect, useState} from "react";
import useTiquets from "../Servicio/Servei_CompraTiquets";
import {Button} from "react-bootstrap";

export default function VistaTiquets() {
    const {vistaTiquets, tiquets} = useTiquets();
    useEffect(() => {
        vistaTiquets();
    },[]);

    const transformaData = (data) =>{
        return   data.substr(8,2) + "/" + data.substr(5,2) + "/" + data.substr(0,4)
    }

    const ComparaDates = (tiquet, day) => {
        if (tiquet.Dia.substr(0,4) >= day.getFullYear()){
            if(tiquet.Dia.substr(5,2) >=  (day.getMonth() + 1)){
                if(tiquet.Dia.substr(8,2) >= day.getDate()){
                    return true
                }else {
                    return false
                }
            }else {
                return false
            }
        }else {
            return false
        }
    }

    const TiquetsActuals = () => {
        if(tiquets != null){
            const date = new Date();
            return tiquets.map((tiquet) => ComparaDates(tiquet, date) ?
                    <div className="row">
                        <div className="col">
                            <p>{transformaData(tiquet.Dia)}</p>
                        </div>
                        <div className="col">
                            <p>{tiquet.IDAlumno}</p>
                        </div>
                        <div className="col">
                            {tiquet.Pagat ? <p>No Pagat</p> : <p>Pagat</p>}
                        </div>
                        <div className="col">
                            <Button>Veure</Button>
                        </div>
                    </div> : null
            )
        }
    }
    return (
        <div>
            <div className="container">
                <div className="row">
                    <div className="col-md-12">
                        <div className="section-heading">
                            <h2>Tiquets</h2>
                        </div>
                    </div>
                </div>
                <div className="row">
                    <div className="col-md-12">
                        <div className="sub-heading">
                            <h4>Tiquets Actuals</h4>
                        </div>
                    </div>
                </div>
                {TiquetsActuals()}
            </div>
    </div>);
}
