import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import {Dropdown} from "reactstrap";
import './Header.css';
import {Link} from 'wouter';
import useUser from "../Servicio/autenticationservice";

export default function Header() {

        const {isLogged, logout} = useUser();

        const handleClick = (e) => {
            e.preventDefault();
            logout();
        }
        return(
            <header className="page-heading header-text container">
                {
                    isLogged ?
                        <Dropdown>
                            <Dropdown.Toggle variant="succes" id="dropdown-basic">
                                {window.sessionStorage.getItem('usuari')}
                            </Dropdown.Toggle>

                            <Dropdown.Menu>
                                <Dropdown.Item onClick={handleClick()}>Tancar sessi√≥</Dropdown.Item>
                                <Dropdown.Item href="#/action-2">Another action</Dropdown.Item>
                                <Dropdown.Item href="#/action-3">Something else</Dropdown.Item>
                            </Dropdown.Menu>
                        </Dropdown> :
                        <Link className="gf-header"  to='/'>
                            Login
                        </Link>
                }
                <Link className="gf-header"  to='/Registre'>Registrar Usuario</Link>
                <div className="row">

                    <div className="col-md-12">
                        <h1>MenjaTickets</h1>
                        <br/>
                        <p> Compra i gestiona facilment els tickets menjador de l'Escola</p>
                    </div>

                </div>
            </header>
        );

}
