import React, {useEffect, useState} from "react";
import 'react-calendar/dist/Calendar.css';
import {forEach} from "react-bootstrap/ElementChildren";
import Calendari from "./Calendari";
import {Button} from "react-bootstrap";

export default function CalendariDinamic () {
    const [iMes,setiMes] = useState(0);
    const [Mes,setMes] = useState("Abril");
    const [any,setAny] = useState("2021");
    const mesos_text = ["Gener", "Febrer", "Març", "Abril", "Maig", "Juny", "Juliol", "Agost", "Setembre", "Octubre", "Novembre", "Desembre"];


    const avançaMes = () => {
        if(iMes !== 11){
            setiMes(iMes + 1);
        }
    }

    const retrocedeixMes = () => {
        if(iMes !== 0){
            setiMes(iMes - 1);
        }
    }

    const createAny = (any) => {
        const dia_actual = new Date();
        setiMes(dia_actual.getMonth());
        const mes = mesos_text[iMes];

        return (<Calendari Any={"2021"} Index={iMes}></Calendari>);
    }


    return <div>
        <div className="Mes">
            <h1>{any}</h1>
            <div className="row">
                <div className="col"><Button onClick={retrocedeixMes()}>-</Button></div>
                <div className="col"><h2>{Mes}</h2></div>
                <div className="col"><Button onClick={avançaMes()}>+</Button></div>
            </div>
        </div>

    </div>
}
