import React, {useEffect} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import useUser from "../Servicio/autenticationservice";
import ServeiVistaInfant from "../Servicio/Servei_VistaInfant";
import HomePageAdmin from "./HomePageAdmin";
import HomePageUsu from "./HomePageUsu";
import {useLocation, Redirect} from "wouter";
import InfantButton from "./InfantButton";

import './HomePage.css';

export default function HomePage () {
    const {isLogged,foundname, user} = useUser();


    useEffect(() => {
        foundname();
    },[isLogged])



    return(
            <div >
                { !isLogged ? <Redirect to={'/'}/> :
                <div className="container">
                    <div className="row">
                        <div className="col-md-12">
                            <div className="section-heading">
                                <h2>Home</h2>
                            </div>
                        </div>
                    </div>
                    <div className="row">

                            {
                                user == 'admin' ?
                                    <HomePageAdmin/>
                                    :
                                    <HomePageUsu/>
                            }
                    </div>
                </div>
                }

        </div>

    );
}
