import React from "react";
import 'react-calendar/dist/Calendar.css';
import {forEach} from "react-bootstrap/ElementChildren";

export default function Calendari () {
    const dies_text = ["DL", "DT", "DC", "DJ", "DV", "DS", "DG"];
    const dies = [];
    const mesos_text = ["Gener", "Febrer", "MarÃ§", "Abril", "Maig", "Juny", "Juliol", "Agost", "Setembre", "Octubre", "Novembre", "Desembre"];
    const messos = [];
    const setmanes = [];

    const createAny = (any) => {
        for (let i = 1; i < 366; i++) {
            let fecha = DataPerDia(any, i);
            let dia = fecha.getDate();
            let dia_semana = fecha.getDay();
            let mesi = fecha.getMonth();
        }

        messos.push(mesos_text.map((mes,index) => createMes(mes, index)));
    }

    const createMes = (any,mes, index) => {
        return (<div className="Mes">
            <title>{mes}</title>
            {createTabla(any, mes, index)}
        </div>)
    }

    const createTabla = (any, mes, index) => {

        return <table>
            {createTitolDies()}
            {createSetmanes(any,mes, index)}
        </table>
    }

    const createTitolDies = () => {
        return (
            <tr>
                {dies_text.map((dia) => <th>{dia}</th>)}
            </tr>
        )
    }

    const createSetmanes = (any, mes, index) =>{
        let setmanes = [];
        let dia = 0;
        for(let i = 0; i < 6; i++){

            setmanes.push(<tr>{dies_text.map(() => <td><button>{new Date(any,mes,dia++)}</button></td>)}</tr>)
        }
        return(setmanes)
    }

    function DataPerDia(any, dia) {
        var date = new Date(any, 0);
        return new Date(date.setDate(dia));
    }



    return(
            <div>
                {createMes()}
            </div>
    )
}
