import React, {useEffect, useState} from 'react';
import {Button, Modal, Table, ToggleButton, ToggleButtonGroup} from "react-bootstrap";
import './PopupCompra.css';
import useTiquets from "../Servicio/Servei_CompraTiquets";
import useMenu from "../Servicio/Servei_Menu";

export default function PopupCompra ({Dies, profiles}) {
    const [show, setShow] = useState(false);
    const [diaInfant, setDiaInfant] = useState();
    const {compraTiquets} = useTiquets();
    const {CategoriesMenu, menus} = useMenu();
    const [preu, setPreu] = useState({});
    const [total, setTotal] = useState(0);

    useEffect(() =>{CategoriesMenu();}, [Dies]);

    const handleClose = () => setShow(false);
    const handleShow = () => {
        setShow(true);
        setDiaInfant(null);
        setPreu(null);
        setTotal(0);
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        compraTiquets(diaInfant);
        handleClose();
    }

    const handleChange = (val) => {
        setDiaInfant(val);
        totalPreu();
        console.log(diaInfant);
    }

    const handleMenu = ({target}) => {
        const  {name, value} = target;
        setPreu((prevPreu) => ({
            ...prevPreu,
            [name]: value
        }));
        setDiaInfant(null);
    }

    const totalPreu = () =>{
        if(diaInfant !== null){
            diaInfant.map((d) => setTotal((prev) => prev + d.split(" ", 3)[2]))
        }
    }

    const tranformatext = (f) => {
        const dia = f.getDate() + "/" + (f.getMonth() + 1) + "/" + f.getFullYear() + ": ";
        return dia;
    }
    const tranformatext2 = (f) => {
        const dia = f.getFullYear()+"-"+f.getMonth()+"-"+ f.getDate();
        return dia;
    }

    const tablaDias = () => {
        return <>
            {filaTipus()}
            {Dies.map((f) => <tr>
            <td className="Dia">
                <p>{tranformatext(f)}</p>
            </td>
            <td className="ButtonName">
                <ToggleButtonGroup type="checkbox" value={diaInfant} onChange={handleChange}>
                    {tablaInfant(f)}
                </ToggleButtonGroup>
            </td>
                <td className="PreuDia">
                    {preuInfant()}
                </td>
        </tr>)}
        </>
    }

    const tablaInfant = (f) => {
        if(profiles !== null){
            return profiles.map((p,i) =>
                <ToggleButton variant={"outline-primary"} value={p.idAlumne + " " + tranformatext2(f) + " " + preuTiquet(i)}>{p.Nom}</ToggleButton>
            )
        }else {
            return <h4>Registra al teu infant</h4>
        }
    }

    const filaTipus = () => {
        if(profiles !== null){
            return <tr>
                <td>
                    Menú:
                </td>
                <td>
                {profiles.map((p, i) =>
                    <select name={i} id={i} onChange={handleMenu} >
                        <option value="0">Selecciona</option>
                        {tipusMenu()}
                    </select>
                )}
                </td>
                <td >
                    Preus per Menu:
                </td>
            </tr>
        }
    }

    const tipusMenu = () => {
        if(menus !== null){
            return menus.map((m) => <option value={m.Preu}>{m.Nom}</option>)
        }
    }

    const preuInfant = () => {
        if (profiles !== null && preu !== null) {
            return profiles.map((p, i) =>
                <p>{preu[i] !== undefined ? preu[i] + "€" : "0€"}</p>
            )
        }
    }

    const preuTiquet = (i) => {
        if (preu !== null) {
            if(preu[i] !== undefined){
                return preu[i];
            }else {
                return "0";
            }
        }else {
            return "0";
        }
    }

    return (
        <>
            <Button variant="primary" onClick={handleShow}>
                Compra
            </Button>
            <Modal show={show}
                   aria-labelledby="contained-modal-title-vcenter"
                   centered
                   className="PopupCompra"
                   onHide={handleClose}>
                <Modal.Header closeButton style={{backgroundColor: "#343a40"}}>
                    <Modal.Title>Selecciona infants</Modal.Title>
                </Modal.Header>
                <form onSubmit={handleSubmit}>
                    <Modal.Body style={{backgroundColor: "#343a40"}}>
                        {Dies.length === 0 ? <h4>Selecciona els dies</h4> :
                            <>
                                <Table borderless>
                                    <tbody>
                                        {tablaDias()}
                                    </tbody>
                                </Table>
                                <h5>Total: {total} </h5>
                            </>}
                    </Modal.Body>
                    <Modal.Footer style={{backgroundColor: "#343a40"}}>
                        <Button variant="danger" onClick={handleClose}>
                            Tancar
                        </Button>
                        <Button variant="success" type="submit">
                            Comprar
                        </Button>
                    </Modal.Footer>
                </form>
            </Modal>
        </>
    );
}
