import React from "react";

export default class LlistaTiquets extends React.Component {


    askPagat = (tiquet) => {
        if(tiquet.Pagat === "1") {
            return " Pagat"
        }else {
            return "";
        }
    }

    render() {
        return (this.props.tiquets.map((t) => <li className={"row Tiquet" + this.askPagat(t)}>
                <p className="col">{t.IDTiquet}</p>
                <p className="col">{t.IDAlumno.Nom + " " + t.IDAlumno.Congnom}</p>
                {t.Pagat === "1" ? <p className="col">Pagat</p> : <p className="col">No Pagat</p>}
                <p className="col">{t.IDTipus_Menu}</p>
                <input className="col" type={"checkbox"}/>
            </li>)
        )
    }
}
