import React, {useEffect, useState} from "react";
import useTiquets from "../Servicio/Servei_CompraTiquets";

export default function VistaTiquets() {
    const {vistaTiquets, tiquets} = useTiquets();
    useEffect(() => {
        vistaTiquets();
    },[]);

    const llistaTiquets = () => {
        if(tiquets !== null){
            return tiquets.map((tiquet) => <h2>{tiquet.Dia}</h2>)
        }

    }
    return (
        <div>
            <div className="container">
                <div className="row">
                    <div className="col-md-12">
                        <div className="section-heading">
                            <h2>Tiquets</h2>
                        </div>
                    </div>
                </div>
                <div className="row">
                    <div className="col-md-12">
                        <div className="section-heading">
                            {llistaTiquets()}
                        </div>
                    </div>
                </div>
            </div>
    </div>);
}
