import React from "react";
import 'react-calendar/dist/Calendar.css';
import {forEach} from "react-bootstrap/ElementChildren";

export default function Calendari () {
    const dies = ["DL", "DT", "DC", "DJ", "DV", "DS", "DG"];
    const mesos = ["Gener", "Febrer", "MarÃ§", "Abril", "Maig", "Juny", "Juliol", "Agost", "Setembre", "Octubre", "Novembre", "Desembre"];
    const setmanes = [];


    const createMes = () => {
        return (<div className="Mes">
            {createTabla()}
        </div>)
    }

    const createTabla = () => {

        return <table>
            {createTitolDies()}
            {createSetmanes()}
        </table>
    }

    const createTitolDies = () => {
        return (
            <tr>
                {dies.map((dia) => <th>{dia}</th>)}
            </tr>
        )
    }

    const createSetmanes = () =>{
        let setmanes = [];
        for(let i = 0; i < 6; i++){
            setmanes.push(<tr>{dies.map(() => <td><button>D</button></td>)}</tr>)
        }
        return(setmanes)
    }

    function DataPerDia(any, dia) {
        var date = new Date(any, 0);
        return new Date(date.setDate(dia));
    }

    
    return(
            <div>
                {createMes()}
            </div>
    )
}
