import React from "react";
import './ListDays.css';
import {Button} from "react-bootstrap";

export default class ListDays extends React.Component {
    componentDidUpdate(p, prevState, snapshot) {
    }

    render() {
        return (<div className="ListaDias">
                <h5>Dies Seleccionats</h5>
                <ul className="container">
                    {this.props.days.map((f, i) =>
                        <div className="row">
                            <div className="col-6">
                                <li>{f.getDate() + "/" + (f.getMonth() + 1) + "/" + f.getFullYear()} </li>
                            </div>
                            <div className="col-4"><Button className="btn-sm" variant="danger" onClick={() => this.props.funcion(i)}>X</Button></div>
                        </div>
                    )}
                </ul>
            </div>
        )
    }
}
