import React, {useState} from 'react';
import Modal from "react-awesome-modal";
import {Button} from "react-bootstrap";
import './PopupAlert.css';
import ServeiVistaInfant from "../Servicio/Servei_VistaInfant";

export default function PopupCompra ({idInfant}) {
    const {deleteInfant} = ServeiVistaInfant();
    const [visible, setVisible] = useState(false);
    const tama単o = {width: 250, height: 150}

    const openModal = () =>  {
        setVisible(true);
    }

    const closeModal = () => {
        setVisible(false)
    }

    const handleClick = () => {
        var item = {};
        item['idAlumne'] = idInfant;
        closeModal();
        deleteInfant(item);
    };


    return (
        <>
            <Button className="FirstButton" value="Open" onClick={openModal}>Compra</Button>
            <Modal
                visible={visible}
                effect="fadeInDown"
                onClickAway={closeModal}
                className="PopupModal"
                width = "250"
                height="150"
            >
                <div className="PopupAlert">
                    <h1>多Vols eliminar al teu fill?</h1>
                    <div className="row">
                        <div className="col">
                            <Button variant="danger"  value="Open" onClick={closeModal}>Cancela</Button>
                        </div>
                        <div className="col">
                            <Button   value="Open" onClick="">Compra</Button>
                        </div>
                    </div>
                </div>
            </Modal>
        </>
    );
}
