import React, {useEffect, useState}from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import {Table, Button, Container, Modal, ModalBody, ModalHeader, NavLink, Dropdown} from "reactstrap";
import './Header.css';
import {Link} from 'wouter';
import useUser from "../Servicio/autenticationservice";
import Context from "../Context/UserContextProvider";

export default function Header() {
    const {isLogged, logout, foundname, user} = useUser();

    useEffect(()=>{
        if(isLogged)foundname();
    },[isLogged])

    const handleclick = e => {
        e.preventDefault();
        logout();
    }

    return(
        <header className="page-heading header-text container">
            {
                isLogged ?
                    <Dropdown>
                        <Dropdown.Toggle variant="success" id="dropdown-basic">
                            {user}
                        </Dropdown.Toggle>

                        <Dropdown.Menu>
                            <Dropdown.Item onClik={handleclick} to={'/'}>Tanca sessi√≥</Dropdown.Item>
                        </Dropdown.Menu>
                    </Dropdown>: null
            }

            <div className="row">

                <div className="col-md-12">
                    <h1>MenjaTickets</h1>
                    <br/>
                    <p> Compra i gestiona facilment els tickets menjador de l'Escola</p>
                </div>

            </div>
        </header>
    );
}
