import React, {useEffect} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import useUser from "../Servicio/autenticationservice";
import ServeiVistaInfant from "../Servicio/Servei_VistaInfant";


import {useLocation, Redirect} from "wouter";
import InfantButton from "./InfantButton";
import VistaInfant from "./VistaInfant";
import './HomePage.css';

export default function HomePage () {
    const {isLogged,foundname, user} = useUser();
    const {profiles, getInfant, fill} = ServeiVistaInfant();
    const [, navigate] =useLocation();


    useEffect(() => {
        foundname();
        getInfant();
    },[isLogged, fill])

    const handleclick = (e) =>{
        e.preventDefault();
        navigate('/Registre')
    }
    const handleclick2 = (e) =>{
        e.preventDefault();
        navigate('/RegistreInfant')
    }

    return(
            <div >
                { !isLogged ? <Redirect to={'/'}/> :
                <div className="container">
                    <div className="row">
                        <div className="col-md-12">
                            <div className="section-heading">
                                <h2>Home</h2>
                            </div>
                        </div>
                    </div>
                    <div className="row">

                            {
                                user != 'admin' ?
                                    <div className="service">
                                        
                                        <VistaInfant fill={fill} profile={profiles}></VistaInfant>
                                        <hr/>
                                        <div className="col-md-12">
                                            <InfantButton name={"Afegir Infant"} onClick={handleclick2}></InfantButton>
                                        </div>
                                    </div>
                                    :
                                    <div>
                                        <div className="col">
                                            <InfantButton name={"Registrar Usuari"} onClick={handleclick}>Afegir Usuari </InfantButton>
                                        </div>
                                        <div className="col">
                                            <InfantButton name={"Afegir Infant"} onClick={handleclick2} ></InfantButton>
                                        </div>
                                    </div>
                            }
                    </div>
                </div>
                }

        </div>

    );
}
