import React, {useState} from 'react';
import {Button, Modal, Table} from "react-bootstrap";
import './PopupCompra.css';

export default function PopupCompra ({Dies, profiles}) {
    const [show, setShow] = useState(false);
    const [diaInfant, setDiaInfant] = useState({});

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const handleSumbit = (event) => {
        event.preventDefault();
        alert(diaInfant);
    }

    const handleChange = ({ target }) => {
        const  {name, value} = target;
        console.log(name);
        setDiaInfant((prev) => ({
            ...prev,
            [name]: value
        }));
    };

    const tablaDias = () => {
        return Dies.map((f) => <tr>
            <td>
                {f.getDate() + "/" + (f.getMonth() + 1) + "/" + f.getFullYear()}
            </td>
            {tablaInfant(f)}
        </tr>)
    }

    const tablaInfant = (f) => {
        return profiles.map((p) => <td className="form-group">
                <input type="checkbox" id="Dias" name={p.Nom + f.getDate() + "/" + (f.getMonth() + 1) + "/" + f.getFullYear()} value={{p, f}} onChange={handleChange}/>
                <label htmlFor="Dias"/>
            </td>
        )
    }


    return (
        <>
            <Button variant="primary" onClick={handleShow}>
                Compra
            </Button>
            <form onSubmit={handleSumbit}>
                <Modal show={show}
                       aria-labelledby="contained-modal-title-vcenter"
                       centered
                       className="PopupCompra"
                       onHide={handleClose}>
                    <Modal.Header closeButton>
                        <Modal.Title>Revisa</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        {Dies.length === 0 ? <h4>Selecciona els dies</h4> :
                            <>
                                <Table>
                                    <thead>
                                    <tr>
                                        <td></td>
                                        {profiles !== null ? profiles.map((p) => <th>{p.Nom}</th>) : <h4>Afegeix als teus infants</h4>}
                                    </tr>
                                    </thead>
                                    <tbody>
                                        {tablaDias()}
                                    </tbody>
                                </Table>
                                <h4>Total: </h4>
                            </>}
                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={handleClose}>
                            Close
                        </Button>
                        <Button variant="primary" type="submit">
                            Save Changes
                        </Button>
                    </Modal.Footer>
                </Modal>
            </form>
        </>
    );
}
