import React, {useState, useEffect} from 'react';
import './Login.css';
import {Button} from "reactstrap";

import { useHistory } from "react-router-dom";

export default function Login(){
    const [profile, setProfile] = useState({});
    let history = useHistory();

    const handleChange = ({ target }) => {
        const  {name, value} = target;
        setProfile((prevProfile) => ({
            ...prevProfile,
            [name]: value
        }));
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        alert(JSON.stringify(profile, '', 2));
        history.push("/App");
    };

    return(
        <div className="login-page">
            <div className="page-heading header-text container">
                <div className="">
                    <div className="row">
                        <div className="col-md-12">
                            <h1>MenjaTickets</h1>
                            <br/>
                            <p> Compra i gestiona facilment els tickets menjador de l'Escola</p>
                        </div>
                    </div>

                </div>
            </div>
            <div className="container">
                <div className="abs-center">
                    <form onSubmit={handleSubmit} className="border p-3 form">
                        <div className="form-group">
                            <h4>Inicia sessió</h4>
                        </div>
                        <div className="form-group">
                            <label htmlFor="email">Usuari</label>
                            <input value={profile.usuari || ''} type="text" name="usuari" id="email" className="form-control" onChange={handleChange}/>
                        </div>
                        <div className="form-group">
                            <label htmlFor="password">Contraseña</label>
                            <input value={profile.contraseña || ''} type="password" name="contraseña" id="password" className="form-control" onChange={handleChange}/>
                        </div>
                        <button type="submit" className="btn btn-primary">Login</button>
                    </form>
                </div>
            </div>

        </div>

    );

}
