import React from "react";
import './ListDays.css';
import {Button} from "react-bootstrap";

export default class ListDays extends React.Component {
    render() {
        return (<div className="ListaDias">
            <h5>Dies Seleccionats</h5>
            <ul>
                {this.props.days.map((f) =>
                    <li type="disc">{f.getDate() + "/" + (f.getMonth() +1) + "/" + f.getFullYear()}<Button className="btn-sm" variant="danger">x</Button></li>)}
            </ul>
        </div>)
    }
}
