import React, {useEffect, useState} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import ServeiVistaInfant from "../Servicio/Servei_VistaInfant";
import {useLocation, Redirect} from "wouter";
import InfantButton from "./InfantButton";
import VistaInfant from "./VistaInfant";
import Calendari from "./Calendari";
import {Button} from "react-bootstrap";


export default function HomePageUsu () {
    const {profiles, getInfant, fill} = ServeiVistaInfant();
    const [, navigate] =useLocation();
    const [iMes,setiMes] = useState(0);
    const mesos_text = ["Gener", "Febrer", "Març", "Abril", "Maig", "Juny", "Juliol", "Agost", "Setembre", "Octubre", "Novembre", "Desembre"];

    useEffect(() => {
        getInfant();
    },[])

    const handleclick = (e) =>{
        e.preventDefault();
        navigate('/RegistreInfant')
    }
    const viewInfant = () =>{
        if(profiles != null) {
            return profiles.map((profile) =>
        <VistaInfant profile={profile}></VistaInfant>)
        }
    }

    const createAny = (any) => {
        const dia_actual = new Date();
        if(iMes === null){
            setiMes(dia_actual.getMonth());
        }
        const mes = mesos_text[iMes];
        return (
            <div className="Mes">
                <h1>{any}</h1>
                <div className="row">
                    <div className="col"><Button onClick={setiMes((prev) => prev !== 0 ? prev - 1: prev)}>-</Button></div>
                    <div className="col"><h2>{mes}</h2></div>
                    <div className="col"><Button onClick={setiMes((prev) => prev !== 11 ? prev + 1: prev)}>+</Button></div>
                </div>
            </div>
        );
    }


    return(
         <div className="service">

                 {fill ? viewInfant() : <h1>Afegeix al teu fill</h1>}
                 <hr/>
             <div className="col-md-12">
                 <InfantButton name={"Afegir Infant"} onClick={handleclick}></InfantButton>
             </div>

             <div className="col-md-12">
                 {createAny("2021")}
                 <Calendari MES={iMes}></Calendari>
             </div>

         </div>
    );
}
