import React, {useEffect, useState} from 'react';
import {Button, Col, Form, FormControl, InputGroup, Modal, Table} from "react-bootstrap";
import './PopupAfegirDiners.css';
import useDiners from "../Servicio/Servei_Diners";

export default function PopupAfegirMenu () {
    const [show, setShow] = useState(false);
    const [Saldo, setSaldo] = useState(null);

    useEffect(() =>{},[])

    const handleClose = () => setShow(false);
    const handleShow = () => {
        setShow(true);
        setSaldo(null);
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        handleClose();
    }
    const handleChange = ({target}) => {
        const {name, value} = target
        console.log(value)
        if (value != null){
            setSaldo(parseInt(value))
        }else {
            setSaldo(parseInt(0))
        }

    }


    return (
        <>
            <a style={{display: "block"}} onClick={handleShow}>
                Afegir Menu
            </a>
            <Modal show={show}
                   aria-labelledby="contained-modal-title-vcenter"
                   centered
                   className="PopupCompra"
                   onHide={handleClose}>
                <Modal.Header closeButton style={{backgroundColor: "#343a40"}}>
                    <Modal.Title>Afegeix un Menú</Modal.Title>
                </Modal.Header>
                <form onSubmit={handleSubmit}>
                    <Modal.Body style={{backgroundColor: "#343a40"}}>
                        <Form>
                            <Form.Group controlId="Primer">
                                <Form.Label>Primer</Form.Label>
                                <Form.Control type="text" placeholder="Primer" />
                            </Form.Group>

                            <Form.Group controlId="Segon">
                                <Form.Label>Segon</Form.Label>
                                <Form.Control type="text" placeholder="Segon" />
                            </Form.Group>
                            <Form.Group controlId="formGridAddress1">
                                <Form.Label>Postre</Form.Label>
                                <Form.Control type="text" placeholder="Postre" />
                            </Form.Group>
                            <Form.Row>
                                <Form.Group as={Col} controlId="Preu">
                                    <Form.Label>Preu</Form.Label>
                                    <Form.Control type="numeric" placeholder="Preu" />
                                </Form.Group>
                            </Form.Row>


                        </Form>
                    </Modal.Body>
                    <Modal.Footer style={{backgroundColor: "#343a40"}}>
                        <Button variant="danger" onClick={handleClose}>
                            Tancar
                        </Button>
                        <Button onClick={handleSubmit} variant="success">
                            Pagar
                        </Button>
                    </Modal.Footer>
                </form>
            </Modal>
        </>
    );
}
