import React, {useEffect, useState}from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import {Dropdown} from "reactstrap";
import './Header.css';
import {useLocation} from "wouter";
import useUser from "../Servicio/autenticationservice";

export default function Header() {
    const {isLogged, logout, foundname, user} = useUser();
    const [, navigate] =useLocation();
    
    useEffect(()=>{
        if(isLogged)foundname();
    },[isLogged])

    const handleclick = e => {
        e.preventDefault();
        logout();
        navigate('/');
    }

    return(
        <header className="page-heading header-text container">
            {
                isLogged ?
                    <Dropdown>
                        <Dropdown.Toggle variant="success" id="dropdown-basic">
                            {user}
                        </Dropdown.Toggle>

                        <Dropdown.Menu>
                            <Dropdown.Item onClik={handleclick}>Tanca sessi√≥</Dropdown.Item>
                        </Dropdown.Menu>
                    </Dropdown>: null
            }

            <div className="row">

                <div className="col-md-12">
                    <h1>MenjaTickets</h1>
                    <br/>
                    <p> Compra i gestiona facilment els tickets menjador de l'Escola</p>
                </div>

            </div>
        </header>
    );
}
