import React, {useEffect} from "react";
import ServeiVistaInfant from "../Servicio/Servei_VistaInfant";

export default function VistaInfant() {
    const {profile, getInfant, fill} = ServeiVistaInfant();

    useEffect(() => {
        getInfant();
    },[fill])


    return(
        <div>
        { fill ?
            <div className="container">
                <div className="row Infant">
                    <div className="col">
                        <h3>{profile.Nom}</h3>
                        <h4>Edat: {profile.Edat}</h4>
                    </div>
                    <div className="col">
                        <h4>{profile.Congnom}</h4>
                        <p>Curs: {profile.Curs}</p>
                    </div>
                    <div className="col">
                        <h4>Observacions:</h4>
                        <p>Curs: {profile.Observacions}</p>
                    </div>
                    <div className="col">
                        <h3>Persona de Contacte: </h3>
                        <h4>Tutor: {profile.Tutor}</h4>
                        <p>Telefon: {profile.telefon}</p>
                    </div>
                </div>
            </div> : <h1>Afegeix al teu fill</h1>
        }
        </div>
    )

}
