import React, {useState} from 'react';
import {Button, Modal} from "react-bootstrap";
import './PopupAlert.css';
import ServeiVistaInfant from "../Servicio/Servei_VistaInfant";

export default function PopupCompra ({Dies, onClick, profiles}) {
    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);



    return (
        <>
            <Button variant="primary" onClick={handleShow}>
                Compra
            </Button>

            <Modal show={show}
                   aria-labelledby="contained-modal-title-vcenter"
                   centered
                   onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Revisa</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <table>
                        <tr><td></td>{profiles !== null ? profiles.map((p) => <th>{p.Nom}</th>) : <h4>Afegeix als teus infants</h4>}</tr>
                        {Dies !== null ? Dies.map((f) => <tr>
                            <td>
                                {f.getDate() + "/" + (f.getMonth() + 1) + "/" + f.getFullYear()}
                            </td>
                            <td>
                                <input type="checkbox" id="vehicle1" name="vehicle1" value="Bike"/>
                                <label htmlFor="vehicle1"> Si </label>
                            </td>
                            <td>
                                <input type="checkbox" id="vehicle1" name="vehicle1" value="Bike"/>
                                <label htmlFor="vehicle1"> No </label>
                                </td>
                        </tr>) : null }
                    </table>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Close
                    </Button>
                    <Button variant="primary" onClick={handleClose}>
                        Save Changes
                    </Button>
                </Modal.Footer>
            </Modal>
        </>
    );
}
