import React from "react";
import 'react-calendar/dist/Calendar.css';
import {forEach} from "react-bootstrap/ElementChildren";

export default function Calendari () {
    const dies_text = ["DL", "DT", "DC", "DJ", "DV", "DS", "DG"];
    const dies = [];
    const mesos_text = ["Gener", "Febrer", "Març", "Abril", "Maig", "Juny", "Juliol", "Agost", "Setembre", "Octubre", "Novembre", "Desembre"];
    const messos = [];
    const setmanes = [];

    const createAny = (any) => {
        for (let i = 1; i < 366; i++) {
            let fecha = DataPerDia(any, i);
            let dia = fecha.getDate();
            let dia_semana = fecha.getDay();
            let mesi = fecha.getMonth();
        }

         messos.push(mesos_text.map((mes,index) => createMes(mes, index)));
    }

    const createMes = (mes, dia) => {
        return (<div className="Mes">
            <h1>{mes}</h1>
            {createTabla(dia)}
        </div>)
    }

    const createTabla = (dia) => {

        return <table>
            {createTitolDies()}
            {createSetmanes(dia)}
        </table>
    }

    const createTitolDies = () => {
        return (
            <tr>
                {dies_text.map((dia) => <th>{dia}</th>)}
            </tr>
        )
    }

    const createSetmanes = (dia) =>{
        for(let i = 0; i < 6; i++){
            setmanes.push(<tr>{createDies(dia)}</tr>)
        }
        return(setmanes)
    }

    const createDies = (dia) => {
        for(let i = 0; i < 1; i++){
            dies.push(<td><button>D</button></td>)
        }
        return(dies)
    }

    function DataPerDia(any, dia) {
        var date = new Date(any, 0);
        return new Date(date.setDate(dia));
    }



    return(
            <div>
                {createMes("Hola")}
            </div>
    )
}
