import React from "react";
import './ListDays.css';
import {Button} from "react-bootstrap";

export default function ListDays ({days, funcion, envia}) {


    return (<div className="ListaDias">
                <h5>Dies Seleccionats</h5>
                <ul className="container">
                    {days.map((f, i) =>
                        <div className="row">
                            <div className="col-6">
                                <li>{f.getDate() + "/" + (f.getMonth() + 1) + "/" + f.getFullYear()} </li>
                            </div>
                            <div className="col-4"><Button className="btn-sm" variant="danger" onClick={() => funcion(i)}>X</Button></div>
                        </div>
                    )}
                </ul>
                <Button className="Compra" type="submit" onClick={envia}>Comprar</Button>
            </div>
        )
    }
