import React, {useState, useEffect} from 'react';
import './Login.css'
import {Button} from "reactstrap";


export default function Login(){
    const [profile, setProfile] = useState({});


    const handleChange = ({ target }) => {
        const  {name, value} = target;
        setProfile((prevProfile) => ({
            ...prevProfile,
            [name]: value
        }));
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        alert(JSON.stringify(profile, '', 2));
    };

    return(
        <div className="login-page">
            <div className="page-heading header-text container">
                <div className="">
                    <div className="row">
                        <div className="col-md-12">
                            <h1>MenjaTickets</h1>
                        </div>
                    </div>

                </div>
            </div>
            <div className="container">
                    <form className={"login"} onSubmit={handleSubmit}>
                        <label className="form-group">
                            <input  className={"col-4"} value={profile.usuari || ''} type="text" name="usuari" placeholder="Usuari" onChange={handleChange} />
                        </label>
                        <label  className="form-group">
                            <input className={"col-4"}  value={profile.contraseña || ''} type="password" name="contraseña" placeholder="Password" value={profile.contraseña || ''}  onChange={handleChange}/>
                        </label>
                        <Button className="form-check" color={"primary"} type="submit" > Enviar</Button>
                    </form>
            </div>

        </div>

    );

}
