import React, {Component} from "react";
import {Accordion, Card} from "react-bootstrap";
import './CurseTitle.css'
export default class CursTitle extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            name: "fas fa-chevron-right",
        };
    }

    rotate = () => {
            if(tiquet.Pagat === "1") {
                return " Pagat"
            }else {
                return "";
            }
    }

    render() {
        return(
            <Accordion className="CursDia">
                <Accordion.Toggle as={Card.Header} eventKey="0">
                    <div className="container">
                        <div className="TitleCursDia row justify-content-between" >
                            <div className={"col-4"}>
                                <h2>{this.props.curs}</h2>
                            </div>
                            <div className="col-1">
                                <i className={this.state.name}/>
                            </div>
                        </div>
                    </div>
                </Accordion.Toggle>
                <Accordion.Collapse eventKey="0">

                    <Card.Body>
                        {this.props.titols !== undefined ? <ul>
                            {this.props.titols()}
                        </ul> : null}

                        {this.props.infants(this.props.curs)}
                    </Card.Body>
                </Accordion.Collapse>
        </Accordion>)
    }
}
