import React, {useState, useEffect} from 'react';
import {useLocation} from "wouter";
import './Registrar.css';
import InfantRegistre from "../Servicio/enviarRegistreInfant";

export default function RegistrarInfant(){
    const [profile, setProfile] = useState({});
    const [, navigate] =useLocation();
    const {registra, registrat} = InfantRegistre();

    useEffect(() =>{
        if(registrat) navigate('/Homepage');
    },[registrat])

    const handleChange = ({ target }) => {
        const  {name, value} = target;
        setProfile((prevProfile) => ({
            ...prevProfile,
            [name]: value
        }));
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        registra(profile)
    };

    return(
        <div>
            <div className="container">
                <div className="abs-center">
                    <form onSubmit={handleSubmit} className=" p-2 form">
                        <div className="form-group">
                            <h4>Afegeix al teu fill</h4>
                        </div>
                        <div className="form-group">
                            <label htmlFor="Nom">Nom</label>
                            <input value={profile.Nom || ''} type="text" name="Nom" id="Nom" className="form-control" onChange={handleChange} maxLength="30"/>
                        </div>
                        <div className="form-group">
                            <label htmlFor="Cognoms">Cognoms</label>
                            <input value={profile.Cognoms || ''} type="text" name="Cognoms" id="Cognoms" className="form-control" onChange={handleChange} maxLength="30"/>
                        </div>
                        <div className="form-group">
                            <label htmlFor="Edat">Edat</label>
                            <input value={profile.Edat || ''} type="number" name="Edat" id="Edat" className="form-control" onChange={handleChange} maxLength="30"/>
                        </div>
                        <div className="form-check">
                            <input value="P3" type="radio" name="Curs" id="P3" className="form-check-input" maxLength="30"/>
                            <label className="form-check-label" htmlFor="P3">P3</label>
                            <input value="P4" type="radio" name="Curs" id="P4" className="form-check-input" maxLength="30"/>
                            <label className="form-check-label" htmlFor="P4">P4</label>
                            <input value="P5" type="radio" name="Curs" id="P5" className="form-check-input" maxLength="30"/>
                            <label className="form-check-label" htmlFor="P5">P5</label>
                            <input value="1r" type="radio" name="Curs" id="1r" className="form-check-input" maxLength="30"/>
                            <label className="form-check-label" htmlFor="1r">1r</label>
                            <input value="2n" type="radio" name="Curs" id="2n" className="form-check-input" maxLength="30"/>
                            <label className="form-check-label" htmlFor="2n">2n</label>
                            <input value="3r" type="radio" name="Curs" id="3r" className="form-check-input" maxLength="30"/>
                            <label className="form-check-label" htmlFor="3r">3r</label>
                            <input value="P3" type="radio" name="Curs" id="P3" className="form-check-input" maxLength="30"/>
                            <label className="form-check-label" htmlFor="P3">P3</label>

                        </div>
                        <div className="form-group">
                            <label htmlFor="Telefon">Teléfon</label>
                            <input value={profile.Telefon || ''} type="number" name="Telefon" id="Telefon" className="form-control" onChange={handleChange} maxLength="30"/>
                        </div>
                        <div className="form-group">
                            <label htmlFor="Observacions">Observacions</label>
                            <input value={profile.Observacions || ''} type="text" name="Observacions" id="Observacions" className="form-control" onChange={handleChange} maxLength="30"/>
                        </div>
                        <button type="submit" className="btn btn-dark">Registrar infant</button>
                    </form>
                </div>
            </div>

        </div>

    );

}
