import React, {useEffect, useState} from "react";
import 'react-calendar/dist/Calendar.css';
import Calendari from "./Calendari";
import {Button} from "react-bootstrap";
import './CalendariDinamic.css';

export default function CalendariDinamic () {
    const [iMes,setiMes] = useState(0);
    const [Mes,setMes] = useState("");
    const [any,setAny] = useState();
    const mesos_text = ["Gener", "Febrer", "Març", "Abril", "Maig", "Juny", "Juliol", "Agost", "Setembre", "Octubre", "Novembre", "Desembre"];

    useEffect(() =>{
        const dia_actual = new Date();
        const indexMes = dia_actual.getMonth();
        setAny(dia_actual.getFullYear());
        setiMes(indexMes);
        setMes(mesos_text[indexMes]);
        console.log(iMes);
    }, [])

    const avançaMes = () => {
        let indexMes = 0;
        if(iMes !== 11){
            indexMes = iMes + 1
            setiMes(indexMes);

        }else {
            indexMes = 0;
            setAny(any + 1);
            setiMes(indexMes);
        }
        setMes(mesos_text[indexMes]);
    }

    const retrocedeixMes = () => {
        let indexMes = 0;
        if(iMes !== 0){
            indexMes = iMes-1;
            setiMes(indexMes);
        }else {
            indexMes = 11;
            setAny(any - 1);
            setiMes(indexMes);
        }
        setMes(mesos_text[indexMes]);
    }


    return(
      <div className="Mes">
          <h1>{any}</h1>
          <div className="row">
              <div className="col-auto">
                  <Button onClick={retrocedeixMes}><i className="fas fa-angle-left"></i></Button>
              </div>
              <div className="col-auto">
                  <h2>{Mes}</h2>
              </div>
              <div className="col-auto">
                  <Button onClick={avançaMes}><i className="fas fa-angle-right"></i></Button>
              </div>
          </div>
              <Calendari Any={any} Index={iMes}/>
      </div>
    )
}
