import React, {useCallback, useContext, useState} from 'react';
import Context from "../Context/UserContextProvider";

export default function ServeiTotsInfants() {
    const {jwt, setJWT} = useContext(Context);
    const profiles = {}
    const [subscrits, setSubscrits] = useState(false);

    const  getInfants = useCallback((curs) => {
        const item = {};
        item['curs'] = curs;
        item['jwt'] = jwt
        const cargoUtil = JSON.stringify(item);
        fetch("http://localhost/TotsInfants.php", {
            method: "POST",
            headers: {
                "Content-Type": 'application/json'
            },
            body: cargoUtil
        }).then(res => {
            return res.json()
        }).then((res) => {
            profiles[curs] = res;
            if(!res){
                
                setSubscrits(false);
            } else {
                setSubscrits(true);
            }
        }).catch(e => alert(e));
    })


    return {
        profiles,
        getInfants,
        subscrits,
    }
}
