import React, {Component} from "react";
import {Accordion, Card} from "react-bootstrap";

export default class CursTitle extends React.Component{
    render() {
        return(
            <Accordion className="CursDia">
                <Accordion.Toggle as={Card.Header} eventKey="0">
                    <div className="container">
                        <div className="TitleCursDia row justify-content-between">
                            <div className={"col-4"}>
                                <h2>{this.props.curs}</h2>
                            </div>
                            <div className="col-1">
                                <i className="fas fa-chevron-right rotate"/>
                            </div>
                        </div>
                    </div>
                </Accordion.Toggle>
                <Accordion.Collapse eventKey="0">

                    <Card.Body>
                        {this.props.titols !== undefined ? <ul>
                            {this.props.titols()}
                        </ul> : null}

                        {this.props.infants(this.props.curs)}
                    </Card.Body>
                </Accordion.Collapse>
        </Accordion>)
    }
}
