import React, {useEffect} from "react";
import ServeiTotsInfants from "../Servicio/Servei_TotsInfants";
import VistaInfant from "./VistaInfant";
import {Accordion, Button} from "react-bootstrap";
import './TotsInfants.css';
import InfantList from "./InfantList";
import InfantButton from "./InfantButton";

export default function  TotsInfants () {

    const {profiles, getInfants, subscrits} = ServeiTotsInfants();
    const cursos = ["P3","P4","P5","1r","2n","3r","4t","5e","6e"];

    useEffect(() =>{
    },[])

    const cursHeading = () => {
        cursos.map((curs) => {
            getInfants(curs)
            if(profiles != null){
                return <div>
                    <h2>{curs}</h2>
                    <div>{viewInfants()}</div>
                </div>
            }
        })
    }

    const viewInfants = () =>{
        if (profiles != null) {
            return profiles.map((profile) =>
                <InfantList profile={profile} />
            )
        } else {
            return <h1>No hi ha infants</h1>
        }

    }



    return(
        <div className="container">
            <div className="row">
                <div className="col-md-12">
                    <div className="section-heading">
                        <h2>Tots els Infants</h2>
                    </div>
                </div>

            </div>

            <div className="row">

                <div className="col-md-12">
                    <ul>{cursHeading()}</ul>
                </div>
            </div>

        </div>
    )

}
