import React, {useEffect, useState} from 'react';
import {Button, Col, Form, FormControl, InputGroup, Modal, Table} from "react-bootstrap";
import './PopupAfegirDiners.css';
import useMenu from "../Servicio/Servei_Menu";

export default function PopupAfegirMenu () {
    const [show, setShow] = useState(false);
    const [menu, setMenu] = useState(null);
    const {afegirMenu} = useMenu();

    useEffect(() =>{},[])

    const handleClose = () => setShow(false);
    const handleShow = () => {
        setShow(true);
        setMenu(null);
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        handleClose();
        if(menu == null){
            alert("Omple la data i el preu com a minim")
        }else {
            afegirMenu(menu);
        }
    }
    const handleChange = ({target}) => {
        const  {name, value} = target;
        setMenu((prevProfile) => ({
            ...prevProfile,
            [name]: value
        }));

    }


    return (
        <>
            <a style={{display: "block"}} onClick={handleShow}>
                Afegir Menu
            </a>
            <Modal show={show}
                   aria-labelledby="contained-modal-title-vcenter"
                   centered
                   className="PopupCompra"
                   onHide={handleClose}>
                <Modal.Header closeButton style={{backgroundColor: "#343a40"}}>
                    <Modal.Title>Afegeix un Menú</Modal.Title>
                </Modal.Header>
                    <Modal.Body style={{backgroundColor: "#343a40"}}>
                        <Form onSubmit={handleSubmit}>
                            <Form.Group controlId="Data">
                                <Form.Label>Data</Form.Label>
                                <Form.Control type="date" name="Data" onChange={handleChange} />
                            </Form.Group>
                            <Form.Group controlId="Primer">
                                <Form.Label>Primer</Form.Label>
                                <Form.Control type="text" name="Primer" placeholder="Primer" onChange={handleChange} />
                            </Form.Group>

                            <Form.Group controlId="Segon">
                                <Form.Label>Segon</Form.Label>
                                <Form.Control type="text" name="Segon" placeholder="Segon" onChange={handleChange} />
                            </Form.Group>
                            <Form.Group controlId="Postre">
                                <Form.Label>Postre</Form.Label>
                                <Form.Control type="text" name="Postre" placeholder="Postre" onChange={handleChange} />
                            </Form.Group>
                            <Form.Row>
                                <Form.Group as={Col} controlId="Preu">
                                    <Form.Label>Preu</Form.Label>
                                    <Form.Control type="numeric" name="Preu" placeholder="Preu" onChange={handleChange} />
                                </Form.Group>
                                <Form.Group as={Col}>
                                    <Form.Label>Tipus</Form.Label>
                                    <Form.Control
                                        as="select"
                                        className="mr-sm-2"
                                        id="Tipus"
                                        name="Tipus"
                                        onChange={handleChange}
                                    >
                                        <option value="0">Selecciona</option>
                                        <option value="Primaria">Primaria</option>
                                        <option value="Celiac">Celiac</option>
                                        <option value="Halal">Halal</option>
                                        <option value="Infantil">Infantil</option>
                                    </Form.Control>
                                </Form.Group>
                            </Form.Row>
                        </Form>
                        <label htmlFor="Cognoms">Cognoms</label>
                        <input value={ menu.Cognoms || ''} type="text" name="Cognoms" id="Cognoms" className="form-control" onChange={handleChange} maxLength="30" required/>
                    </Modal.Body>
                    <Modal.Footer style={{backgroundColor: "#343a40"}}>
                        <Button variant="danger" onClick={handleClose}>
                            Tancar
                        </Button>
                        <Button onClick={handleSubmit} variant="success">
                            Afegir
                        </Button>
                    </Modal.Footer>
            </Modal>
        </>
    );
}
