import React, {useEffect} from "react";
import ServeiTotsInfants from "../Servicio/Servei_TotsInfants";
import VistaInfant from "./VistaInfant";
import {Accordion, Button} from "react-bootstrap";

export default function  TotsInfants () {

    const {profiles, getInfants, subscrits} = ServeiTotsInfants();

    useEffect(() => {
        getInfants();
    },[subscrits])

    const viewInfant = () =>{
        if(profiles != null) {
            return profiles.map((profile) =>
                <Accordion>
                    <Accordion.Toggle eventKey="0" as={Button} variant={"link"}>
                        <p>{profile.Nom} {profile.Congnom}</p>
                    </Accordion.Toggle>
                    <Accordion.Collapse eventKey="0">
                        <p>{profile.Tutor} {profile.telefon}</p>
                    </Accordion.Collapse>
                </Accordion>
            )
        }
    }

    return(
        <div className="container">
            <div className="row">
                <div className="col-md-12">
                    <div className="section-heading">
                        <h2>Tots els Infants</h2>
                    </div>
                </div>

            </div>

            <div className="row">
                <div className="col-md-12">
                    {viewInfant()}
                </div>
            </div>

        </div>
    )

}
