import React from "react";
import Dropdown from 'react-bootstrap/Dropdown';
import {Nav, Navbar, NavDropdown} from "react-bootstrap";
import {Link} from "wouter";
import PopupAfegirDiners from "./PopupAfegirDiners";

export class Navegador extends React.Component {
    componentDidMount() {
        this.props.funcionsaldo();
    }

    componentDidUpdate(prevProps, prevState, snapshot) {
    }

    render() {
        return(
            <Navbar className="NavMenu" bg="dark" expand="lg" variant="dark">
                <div className="container">
                <Navbar.Brand href="/Homepage">
                    <img
                        src="https://upload.wikimedia.org/wikipedia/commons/thumb/a/a7/React-icon.svg/1280px-React-icon.svg.png"
                        width="45"
                        height="30"
                        className="d-inline-block align-top"
                        alt="React Bootstrap logo"
                    />MenjaTickets</Navbar.Brand>
                <Navbar.Toggle aria-controls="basic-navbar-nav" />
                <Navbar.Collapse id="basic-navbar-nav">
                    <Nav className="mr-auto">
                        <Nav.Link href={"/Homepage"}>Inici</Nav.Link>
                        {this.props.rol === 'Admin' ? <Nav.Link href={"/TotsInfants"}>Infants</Nav.Link> : <Nav.Link href="#link">Reserves</Nav.Link> }
                        <NavDropdown title={this.props.name} id="basic-nav-dropdown">
                            {this.props.rol === 'Admin' ? <NavDropdown.Item>Afegir Menu</NavDropdown.Item> : <NavDropdown.Item><PopupAfegirDiners/></NavDropdown.Item>}
                            <NavDropdown.Item>Canviar Contrasenya</NavDropdown.Item>
                            <NavDropdown.Divider />
                            <NavDropdown.Item onClick={this.props.onClick}>Tanca sessi√≥</NavDropdown.Item>
                        </NavDropdown>
                        {this.props.rol !== 'Admin' ? <Nav.Link >Disponible: {this.props.saldo}‚Ç¨</Nav.Link> : null }
                    </Nav>
                </Navbar.Collapse>
                </div>
            </Navbar>


        );
    }
}
