import React, {useState} from 'react';
import Modal from "react-awesome-modal";
import {Button} from "react-bootstrap";
import './PopupAlert.css';
import ServeiVistaInfant from "../Servicio/Servei_VistaInfant";

export default function PopupCompra ({Dies, onClick}) {
    const {profiles} = ServeiVistaInfant();
    const [visible, setVisible] = useState(false);

    const openModal = () =>  {
        setVisible(true);
    }

    const closeModal = () => {
        setVisible(false)
    }



    return (
        <>
            <Button className="FirstButton" value="Open" onClick={openModal}>Compra</Button>
            <Modal
                visible={visible}
                effect="fadeInDown"
                onClickAway={closeModal}
                className="PopupModal"
                width = "250"
                height="150"
            >
                <div className="PopupAlert">
                    <ul>
                        {Dies.map((f) => <li>{f.getDate() + "/" + (f.getMonth() + 1) + "/" + f.getFullYear()}</li>)}
                    </ul>
                    <div className="row">
                        <div className="col">
                            <Button variant="danger"  value="Open" onClick={closeModal}>Cancela</Button>
                        </div>
                        <div className="col">
                            <Button   value="Open" onClick={onClick}>Compra</Button>
                        </div>
                    </div>
                </div>
            </Modal>
        </>
    );
}
