import React, {useEffect} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Table, Button, Container, Modal, ModalBody, ModalHeader} from "reactstrap";
import useUser from "../Servicio/autenticationservice";
import {useLocation} from "wouter";

export default function HomePage () {
    const {isLogged, user,foundname} = useUser();
    const [, navigate] =useLocation();

    useEffect(() => {
        foundname()
    },[isLogged])

    const handleclick = (e) =>{
        e.preventDefault();

        navigate('/Registre')
    }

    return(

            <div className="container">
                <div className="row">
                    <div className="col-5">
                        {
                            isLogged ?  <div>
                                <h1>Nom</h1>
                                <h1>Curs</h1>
                                <Button className="btn btn-dark">Editar perfil</Button>
                            </div> : null
                        }
                    </div>
                    <div className="col-5">
                        {
                            user == 'admin' ? <div>
                                <Button className ="btn btn-dark" onClick={handleclick}>Afegir Usuari </Button>
                            </div> : null
                        }
                    </div>

                </div>
            </div>

    );
}
