import React from "react";
import './ListDays.css';
import {Button} from "react-bootstrap";

export default class ListDays extends React.Component {
    render() {
        return (<div className="ListaDia ">
            <h5>Dies Seleccionats</h5>
            <ul className="container">
                {this.props.days.map((f) =>
                    <div className="row">
                        <li>{f.getDate() + "/" + (f.getMonth() +1) + "/" + f.getFullYear()} </li>

                        <Button className="btn-sm" variant="danger">X</Button>
                    </div>
                   )}
            </ul>
        </div>)
    }
}
