import React, { Component } from 'react';
import Modal from "react-awesome-modal";
import {Button} from "react-bootstrap";
import './PopupAlert.css';
import ServeiVistaInfant from "../Servicio/Servei_VistaInfant";

export default class PopupAlert extends Component {
    constructor(props) {
        super(props);
        const {deleteInfant} = ServeiVistaInfant();
        this.state = {
            visible : false,
            delete: deleteInfant()
        }
    }
    openModal() {
        this.setState({
            visible : true
        });
    }

    closeModal() {
        this.setState({
            visible : false
        });
    }

    deleteInfant() {
        this.state.delete(this.props.idInfant);
    }

    render() {
        return (
            <>
                <Button variant="danger"  value="Open" onClick={() => this.openModal()}>Elimina</Button>
                <Modal
                    visible={this.state.visible}
                    width="400"
                    height="300"
                    effect="fadeInDown"
                    onClickAway={() => this.closeModal()}
                >
                    <div className="PopupAlert">
                        <h1>Â¿Vols eliminar al teu fill?</h1>
                        <div className="row">
                            <div className="col">
                                <Button variant="success"  value="Open" onClick={() => this.closeModal()}>Cancela</Button>
                            </div>
                            <div className="col">
                                <Button variant="danger"  value="Open" onClick={() => this.deleteInfant()}>Elimina</Button>
                            </div>
                        </div>
                    </div>
                </Modal>
            </>
        );
    }
}
